	public static class ExtractMapper extends Mapper<Text, Text, Text, IntWritable> {
		
		private HashSet<String> needed = new HashSet<>();
		
		@Override
		protected void setup(Context context) throws IOException ,InterruptedException {
			for(String item:toKeep){
				needed.add(item);
			}

		};

		public void map(Text year, Text nGramLine, Context context)
				throws IOException, InterruptedException {
			String[] both = nGramLine.toString().split("\\s");
			String ngram = both[0];
			if(needed.contains(ngram)){
				context.write(new Text(year.toString()+":"+ngram), new IntWritable(Integer.parseInt(both[1])));
			}
		}
	}
	
	public static class ExtractReducer extends Reducer<Text, IntWritable, Text, IntWritable> {
		
		private MultipleOutputs<Text, IntWritable> mout;
		
		@Override
		public void setup(Context context) {
			 mout = new MultipleOutputs<Text, IntWritable>(context);
		}
		
		public void reduce(Text key, Iterable<IntWritable> values, Context context)
				throws IOException, InterruptedException {
			int sum = 0;
			String[] parts = key.toString().split(":");
			String year = parts[0].replace(".", "");
			String ngram = parts[1];
			for(IntWritable freq:values){
				sum += freq.get();
			}
			context.write(new Text(year+"\t"+ngram), new IntWritable(sum));
		}
		
		@Override
		public void cleanup(Context context) {
			try {
				mout.close();
			} catch (IOException | InterruptedException e) {
				e.printStackTrace();
			}
		}
	}

	public static Job createExtractionJob(Path in, Path out) throws IOException {
		Configuration conf = new Configuration();
		Job job = Job.getInstance(conf, "Replace in n-grams");
		job.setJarByClass(ExtractNeededNgrams.class);
		job.setMapperClass(ExtractMapper.class);
		job.setReducerClass(ExtractReducer.class);
		job.setOutputKeyClass(Text.class);
		job.setOutputValueClass(IntWritable.class);
		job.setNumReduceTasks(1);

		job.setInputFormatClass(YearAwareInputFormat.class);
		out.getFileSystem(conf).delete(out, true);
		int n = 5;
		String[] paths = new String[n];
		for(int i=1; i<=n; i++){
			paths[i-1] = in.toString()+in.SEPARATOR+i+"-grams";
		}
		System.out.println(StringUtils.join(",", paths));
		FileInputFormat.addInputPaths(job, StringUtils.join(",", paths));
		FileOutputFormat.setOutputPath(job, out);
		return job;
	}
	
	public static void main(String[] args) throws IllegalArgumentException, IOException, ClassNotFoundException, InterruptedException{
		Job job =  createExtractionJob(new Path(args[0]), new Path(args[1]));
		System.exit(job.waitForCompletion(true) ? 0 : 1);
	}
